import{_ as e,o as a,c as t,Q as r}from"./chunks/framework.9b0cfcaf.js";const p=JSON.parse('{"title":"Интеграция с Алисой Яндекс (в разработке)","description":"","frontmatter":{},"headers":[],"relativePath":"integrations/yandex.md","filePath":"integrations/yandex.md"}'),n={name:"integrations/yandex.md"},o=r('<h1 id="интеграция-с-алисои-яндекс-в-разработке" tabindex="-1">Интеграция с Алисой Яндекс (в разработке) <a class="header-anchor" href="#интеграция-с-алисои-яндекс-в-разработке" aria-label="Permalink to &quot;Интеграция с Алисой Яндекс (в разработке)&quot;">​</a></h1><p>Пока нативная поддержка голосового уаправления Алисой в разработке, можно воспользоваться следующими вариантами:</p><ol><li>Навык <a href="https://yaha-cloud.ru/" target="_blank" rel="noreferrer">Yandex Smart Home</a>. Это компонент Home Assistant, который позволяет добавить его устройства в платформу умного дома Яндекса (УДЯ) и управлять ими с любого устройства с Алисой. Облачное решение.</li><li>Навык <a href="/Gateway/">Домовенок Кузя</a>. Это навык УДЯ, который позволяет подключить голосовое управление практически к любому устройству. Работает по протоколам HTTP GET, MQTT (без подписки на топики) и IFTTT. Облачное решение.</li><li>Написать свой навык, например как <a href="https://github.com/tsurkan-av/SLS/blob/main/AliceSkills/funtik/" target="_blank" rel="noreferrer">Преступник Фунтик</a>. Позволяет интегрировать в УДЯ всё что угодно - зависит от фантазии разработчика. Навык можно развернуть где угодно, где есть node.js. Например, Yandex Cloud Functions.</li></ol>',3),i=[o];function s(l,_,c,d,h,f){return a(),t("div",null,i)}const u=e(n,[["render",s]]);export{p as __pageData,u as default};
